// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "../vrf-solidity-master/contracts/VRF.sol";  // Importa la libreria VRF;

import "hardhat/console.sol";

contract VRFExample {
    using VRF for uint256[2];
    using VRF for uint256[4];

    event RandomNumberGenerated(bytes32 randomNumber);
    event VRFProofGenerated(uint256[4] proof);
    event VRFProofVerified(bool verified);

    
    // Genera un numero casuale usando il VRF e restituisce la proof
   function generateRandomNumber(bytes memory _message, uint256[2] memory publicKey, uint256 privKey) public returns (bytes32, uint256[4] memory) {
    // Log prima della generazione di gamma
    console.log("Inizio calcolo di gamma");
    
    (uint256 gammaX, uint256 gammaY) = VRF.hashToTryAndIncrement(publicKey, _message);
    
    console.log("Calcolo di gamma completato");

    // Step 2: Genera il nonce casuale `k`
    uint256 k = uint256(keccak256(abi.encodePacked(block.timestamp, msg.sender))) % VRF.NN;
    
    // Log prima del calcolo dei punti U e V
    console.log("Inizio calcolo dei punti U e V");

    // Step 3: Calcola i punti U e V
    (uint256 uPointX, uint256 uPointY) = VRF.derivePoint(k, VRF.GX, VRF.GY);  // U = k * G
    (uint256 vPointX, uint256 vPointY) = VRF.derivePoint(k, gammaX, gammaY);  // V = k * H(message)

    console.log("Calcolo dei punti U e V completato");

    // Step 4: Calcola la challenge `c` come hash dei punti
    bytes16 derivedC = VRF.hashPoints(VRF.GX, VRF.GY, gammaX, gammaY, uPointX, uPointY, vPointX, vPointY);

    // Conversione di `bytes16` a `uint256`
    uint256 c;
    assembly {
        c := mload(add(derivedC, 0x10)) // Prende i primi 16 byte (128 bit) e li converte in uint256
    }

    // Step 5: Calcola `s = k + c * privKey mod n`
    uint256 s = addmod(k, mulmod(c, privKey, VRF.NN), VRF.NN);  // k + c * privKey mod NN

    // Step 6: Crea la proof [gammaX, gammaY, c, s]
    uint256[4] memory proof = [gammaX, gammaY, c, s];

    // Step 7: Calcola il valore casuale hashato (random number)
    bytes32 randomNumber = VRF.gammaToHash(gammaX, gammaY);

    // Log per vedere il numero casuale generato
    console.logBytes32(randomNumber);

    // Step 8: Emissione degli eventi
    emit RandomNumberGenerated(randomNumber);
    emit VRFProofGenerated(proof);

    return (randomNumber, proof);
}




function generateRandomNumber2(bytes memory _message, uint256[2] memory publicKey) public returns (bytes32, uint256[4] memory) {
        // Step 1: Genera il punto gamma
        (uint256 gammaX, uint256 gammaY) = VRF.hashToTryAndIncrement(publicKey, _message);
        
        // Step 2: Genera i valori s, c (questi devono essere calcolati off-chain o con un ulteriore algoritmo)
        uint256 s = 1234567890;  // Solo un esempio, questo deve essere calcolato off-chain o in modo sicuro
        uint256 c = 987654321;   // Solo un esempio, questo deve essere calcolato off-chain o in modo sicuro
        
        // Step 3: Crea la proof [gammaX, gammaY, c, s]
        uint256[4] memory proof = [gammaX, gammaY, c, s];

        // Step 4: Calcola il valore casuale hashato (random number)
        bytes32 randomNumber = VRF.gammaToHash(gammaX, gammaY);

        emit RandomNumberGenerated(randomNumber);
        emit VRFProofGenerated(proof);

        return (randomNumber, proof);
    }

    
    // Verifica una proof VRF
    function verifyProof(bytes memory _message, uint256[2] memory publicKey, uint256[4] memory proof) public returns (bool) {
        // Step 1: Verifica la proof
        bool isValid = VRF.verify(publicKey, proof, _message);

        emit VRFProofVerified(isValid);
        return isValid;
    } 
}

